public class BinaryNumbers {

	/**
	 * @param args
	 */
	public static void main(String[] args) {
		// TODO Auto-generated method stub
		System.out.format("%-7s %15s%20s", "Decimal", "Binary","Hexadecimal\n");
		System.out.format("%7s%14s%16s", "1","1","1\n");
		System.out.format("%7s%14s%16s", "2","10","2\n");
		System.out.format("%7s%14s%16s", "3","11","3\n");
		System.out.format("%7s%14s%16s", "4","100","4\n");
		System.out.format("%7s%14s%16s", "5","101","5\n");
		System.out.format("%7s%14s%16s", "6","110","6\n");
		System.out.format("%7s%14s%16s", "7","111","7\n");
		System.out.format("%7s%14s%16s", "8","1000","8\n");
		System.out.format("%7s%14s%16s", "9","1001","9\n");
		System.out.format("%7s%14s%16s", "10","1010","A\n");
		System.out.format("%7s%14s%16s", "11","1011","B\n");
		System.out.format("%7s%14s%16s", "12","1100","C\n");
		System.out.format("%7s%14s%16s", "13","1101","D\n");
		System.out.format("%7s%14s%16s", "14","1110","E\n");
		System.out.format("%7s%14s%16s", "15","1111","F\n");
		System.out.format("%7s%14s%16s", "16","10000","10\n");
		System.out.format("%7s%14s%16s", "17","10001","11\n");
		System.out.format("%7s%14s%16s", "18","10010","12\n");
		System.out.format("%7s%14s%16s", "19","10011","13\n");
		System.out.format("%7s%14s%15s", "20","10100","14");
		
		
		
		

	}

}
