public class BingoRules {

	/**
	 * @param args
	 */
	public static void main(String[] args) {
		// TODO Auto-generated method stub
		System.out.format("Bingo Card\n");

		System.out.format("\n");

		System.out.format("1. The caller randomly pulls numbered bingo ball.\n");

		System.out.format("\n");

		System.out.format("2. The number is placed on the bingo board and called out.\n");

		System.out.format("\n");

		System.out.format("3. Players look for the called number on thier bingo card.\n");

		System.out.format("\n");

		System.out.format("4. If the number is located, it is marked off.\n");

		System.out.format("\n");

		System.out.format("5. Steps 1 to 4 are repeated untill a player matches the BINGO pattern.\n");

		System.out.format("\n");

		System.out.format("6. The winnging player yells BINGO!.");
	}

}
